import React, {useEffect, useState} from "react";
import {Link} from "react-router-dom";
import Header from "../components/Header";
import "../style/SignUp.scss"

const SignUp = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [emailDirty, setEmailDirty] = useState(false);
    const [passwordDirty, setPasswordDirty] = useState(false);
    const [emailError, setEmailError] = useState('Email cannot be empty');
    const [passwordError, setPasswordError] = useState('Password cannot be empty');
    const [formValid, setFormValid] = useState(false);
    const [name, setName] = useState('');

    useEffect(() => {
        if (emailError || passwordError) {
            setFormValid(false);
        } else {
            setFormValid(true);
        }
    }, [emailError, passwordError]);

    const emailHandler = (e: { target: { value: React.SetStateAction<string>; }; }) => {
        setEmail(e.target.value);
        const re =
            /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
        if (!re.test(String(e.target.value).toLowerCase())) {
            setEmailError('Email is not valid');
        } else {
            setEmailError('');
        }
    }

    const passwordHandler = (e: { target: { value: React.SetStateAction<string>; }; }) => {
        setPassword(e.target.value);
        if (e.target.value.length < 8 || e.target.value.length > 16) {
            setPasswordError('Password must be between 8 and 16 characters');
            if (!e.target.value) {
                setPasswordError('Password cannot be empty');
            }
        } else {
            setPasswordError('');
        }
    }

    const blurHandler = (e: { target: { name: React.SetStateAction<string>; }; }) => {
        switch (e.target.name) {
            case 'email':
                setEmailDirty(true);
                break;
            case 'password':
                setPasswordDirty(true);
                break;
        }
    }
    const handleSubmit = (e: { preventDefault: () => void; }) => {
        e.preventDefault();
        console.log(email);
    }

    return (
        <main className='signup'>
            <Header/>
            <div className='signup-page_form-wrapper'>
                <h2 className='signup_header'>Welcome to RPC!</h2>
                <p className='signup_subtext'>Create your account and start saving time today!</p>
                <form className="signup-form" onSubmit={handleSubmit}>
                    <label htmlFor="name">Full name</label>
                    <input value={name} name="name" onChange={(e) => setName(e.target.value)} id="name"
                           placeholder="Full Name"/>
                    <label htmlFor="email">Email</label>
                    {emailDirty && emailError && <div style={{color: 'red'}}>{emailError}</div>}
                    <input value={email}
                           type="email"
                           id="email" name="email"
                           placeholder="Email Address"
                           onChange={(e) => emailHandler(e)}
                           onBlur={e => blurHandler(e)}
                    />
                    <label htmlFor="password">Password</label>
                    {passwordDirty && passwordError && <div style={{color: 'red'}}>{passwordError}</div>}
                    <input value={password}
                           type="password"
                           placeholder="Password"
                           id="password"
                           name="password"
                           onChange={e => passwordHandler(e)}
                           onBlur={e => blurHandler(e)}
                    />
                    <button className="signup-button_submit" type="submit" disabled={!formValid}>Sign Up</button>
                </form>
                <Link to="/Login" className="signup-link_login">Already have an account? Sign in. </Link>
            </div>
        </main>
    )
}

export default SignUp;
