import React, {useState} from "react";
import Footer from "../Footer";

export const Login = (props: { onFormSwitch: (arg0: string) => void; }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [emailDirty, setEmailDirty] = useState('false');
    const [passwordDirty, setPasswordDirty] = useState('false');
    const [emailError, setEmailError] = useState('Email cannot be empty');
    const [passwordError, setPasswordError] = useState('Password cannot be empty');

    const emailHandler = (e: { target: { value: React.SetStateAction<string>; }; }) => {
        setEmail(e.target.value);
        const re =
            /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
        if (!re.test(String(e.target.value).toLowerCase())) {
            setEmailError('Email is not valid');
        } else {
            setEmailError('');
        }
    }

    const passwordHandler = (e: { target: { value: React.SetStateAction<string>; }; }) => {
        setPassword(e.target.value);
        if (e.target.value.length < 8 || e.target.value.length > 16) {
            setPasswordError('Password must be between 8 and 16 characters');
            if (!e.target.value) {
                setPasswordError('Password cannot be empty');
            }
        }
    }

    const blurHandler = (e: { target: { name: any; }; }) => {
        if (e.target.name === 'email') {
            setEmailDirty(true);
        } else if (e.target.name === 'password') {
            setPasswordDirty(true);
        }
    }

    const handleSubmit = (e: { preventDefault: () => void; }) => {
        e.preventDefault();
        console.log(email);
    }

    return (
        <main className='login_container'>
            <div className="login_form-wrapper">
                <h2 className='login_header'>Welcome back!</h2>
                <p className='login_subtext'>Sign in and get to it.</p>
                <form onSubmit={handleSubmit}>
                    <label htmlFor="email">Email</label>
                    {emailDirty && emailError && <div style={{color: 'red'}}>{emailError}</div>}
                    <input className='login-field_email'
                           value={email}
                           type="email"
                           id="email" name="email"
                           placeholder="Email Address"
                           onChange={(e) => emailHandler(e)}
                           onBlur={e => blurHandler(e)}
                    />
                    <label htmlFor="password">Password</label>
                    {passwordDirty && passwordError && <div style={{color: 'red'}}>{passwordError}</div>}
                    <input className='login-field_password'
                           value={password}
                           type="password"
                           placeholder="Password"
                           id="password"
                           name="password"
                           onChange={e => passwordHandler(e)}
                           onBlur={e => blurHandler(e)}
                    />
                    <button className='login-button_submit' type="submit">Sign In</button>
                    <button className="link-btn" onClick={() => props.onFormSwitch('register')}>Don't have an account?
                        Create one now!
                    </button>
                </form>

            </div>
            <Footer/>
        </main>
    )
}
