import React, { useState } from "react";
import './Style/App.css';
import  Login from "./user/Login";
import  SignUp  from "./user/SignUp";
import { Routes, Route } from "react-router-dom";
import  ForgotPassword  from "./user/ForgotPassword";

function App() {
  const [currentForm, setCurrentForm] = useState('login');

  const toggleForm = (formName: React.SetStateAction<string>) => {
    setCurrentForm(formName);
  }

  return (
      <Routes>
        <Route path="/" element={<Login onFormSwitch={toggleForm} />} />
        <Route path="/signup" element={<SignUp onFormSwitch={toggleForm} />} />
        <Route path="/forgotpassword" element={<ForgotPassword onFormSwitch={toggleForm} />} />

      </Routes>
      // <div className="App">
      //   {
      //     currentForm === "login" ? <Login onFormSwitch={toggleForm} /> : <SignUp onFormSwitch={toggleForm} />
      //   }
      // </div>
  );
}

export default App;
